    msg.payload.type = flow.get("class");

if( flow.get("age") ) {
    if( flow.get("age") > 0 ) {
        msg.payload.age = {
            $gte: flow.get("age")
        }
    }
    if( flow.get("age") < 0 ) {
        msg.payload.age = {
            $lte: Math.abs(flow.get("age"))
        }
    }
}

if( flow.get("vertical") ) {
    if(flow.get("vertical") === "Up" ) {
        msg.payload.dy = {
            $lte: 0
        }
    }
    if(flow.get("vertical") === "Down" ) {
        msg.payload.dy = {
            $gte: 0
        }
    }
}

if( flow.get("horizontal") ) {
    if(flow.get("horizontal") === "Left" ) {
        msg.payload.dx = {
            $lte: 0
        }
    }
    if(flow.get("horizontal") === "Right" ) {
        msg.payload.dx = {
            $gte: 0
        }
    }
}

if( flow.get("distance") ) {
    if( flow.get("distance") > 0 ) {
        msg.payload.distance = {
            $gte: flow.get("distance")
        }
    }
    if( flow.get("distance") < 0 ) {
        msg.payload.distance = {
            $lte: Math.abs(flow.get("distance"))
        }
    }
}

return msg;

