var list = [];

msg.payload.forEach(function(object){
    if( flow.get("birth") ) {
        var birth = flow.get("birth");
        var first = object.path[0];
        if( first.x < birth.x1 || first.x > birth.x2)
            return;
        if( first.y < birth.y1 || first.y > birth.y2 )
            return;
    }

    if( flow.get("death") ) {
        var death = flow.get("death");
        var last = object.path[object.path.length-1];
        if( last.x < death.x1 || last.x > death.x2) 
            return;
        if( last.y < death.y1 || last.y > death.y2 )
            return;
    }

    if( flow.get("type") ) {
        var type = flow.get("type");
        if( flow.get("type") === "Vehicle") {
            if(  object.vehicle !== true  )
                return;
        } else {
            if( object.type !== type )
                return;
        }
    }

    if( flow.get("direction")) {
        direction = flow.get("direction");

        if( direction && direction === "Up" && object.dy > 0 )
            return;

        if( direction && direction === "Down" && object.dy < 0 )
            return;

        if( direction && direction === "Left" && object.dx > 0 )
            return;

        if( direction && direction === "Right" && object.dx < 0 )
            return;
    }

    if( flow.get("age") ) {
        var age = flow.get("age");

        if( age > 0 && object.age < age )
            return;
        if( age < 0 && object.age > -age )
            return;
    }

    if( flow.get("distance") ) {
        var distance = flow.get("distance");

        if( distance > 0 && object.distance < distance )
            return;
        if( distance < 0 && object.distance > -distance )
            return;
    }

    if( flow.get("speed") ) {
        var speed = flow.get("speed");

        if( speed > 0 && object.speed < speed )
            return;
        if( speed < 0 && object.speed > -speed )
            return;
    }

    list.push(object);
});

msg.payload = list;
return msg;

