msg.topic = "paths";
var paths = flow.get("paths");
var birth = flow.get("birth") || false;
var death = flow.get("death") || false;

var list = [];

paths.forEach(function(object){
    if( birth ) {
        var first = object.path[0];
        if( first.x < birth.x1 || first.x > birth.x2)
            return;
        if( first.y < birth.y1 || first.y > birth.y2 )
            return;
    }

    if( death ) {
        var last = object.path[object.path.length-1];
        if( last.x < death.x1 || last.x > death.x2) 
            return;
        if( last.y < death.y1 || last.y > death.y2 )
            return;
    }

    var d = new Date(object.timestamp);
    var localTime = d.getFullYear()+"-"+('0'+(d.getMonth()+1)).substr(-2,2)+"-"+('0'+d.getDate()).substr(-2,2) + " " +('0'+d.getHours()).substr(-2,2)+":"+('0'+d.getMinutes()).substr(-2,2);
    item = {
        id: object.id,
        device: object.device,
        timestamp: object.timestamp,
        time: localTime,
        age: object.age,
        distance: object.distance,
        path:object.path,
        type: object.type,
        speed: object.maxSpeed || parseInt((object.distance/object.age))
    }
    list.push(item);
});

msg.payload = list;
return msg;

